import React, { useState } from 'react';

function App() {
  const [palette, setPalette] = useState([]); // State to hold the generated color palette
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Function to call the backend and fetch the color palette
  const handleGenerate = async () => {
    setLoading(true);
    setError(null);

    try {
      // Send a request to the backend for color palette generation
      const response = await fetch('/api/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt: 'Generate a color palette for a website' }), // Customize prompt as needed
      });

      const data = await response.json();

      // Assuming the backend returns a list of colors
      setPalette(data.result); // Store the color palette in the state
    } catch (error) {
      setError('Failed to generate color palette');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container" style={{ textAlign: 'center', padding: '20px' }}>
      <h1>AI Color Palette Generator</h1>
      <button
        onClick={handleGenerate}
        disabled={loading}
        style={{
          padding: '10px 20px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        {loading ? 'Generating...' : 'Generate Palette'}
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Display the generated color palette */}
      {palette.length > 0 && (
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          {palette.map((color, index) => (
            <div
              key={index}
              style={{
                width: '50px',
                height: '50px',
                backgroundColor: `rgb(${color[0]}, ${color[1]}, ${color[2]})`,
                margin: '0 10px',
                borderRadius: '5px',
              }}
            ></div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
