import React, { useState } from 'react';
import './index.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [result, setResult] = useState('');

  const handleGeneratePalette = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/generate-palette', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt }),
      });

      const data = await response.json();

      if (data.palette) {
        setResult(data.palette); // Store the palette array
      } else if (data.error) {
        setResult([data.error]); // Display the error message as a single-item array
      } else {
        setResult(['Unexpected response format']);
      }
    } catch (error) {
      console.error('Error:', error);
      setResult(['An error occurred']);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center p-4 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold text-indigo-600 mb-6">WebMuse</h1>

      <div className="flex items-center bg-white border border-gray-300 rounded-lg shadow-sm w-full max-w-md">
        <input
          type="text"
          placeholder="Type your prompt here..."
          className="flex-1 p-3 focus:outline-none focus:ring focus:ring-indigo-200 rounded-l-lg"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
        />
        <button
          className="px-4 py-2 text-white bg-indigo-600 hover:bg-indigo-700 rounded-r-lg"
          onClick={handleGeneratePalette}
        >
          Generate
        </button>
      </div>

      <div className="mt-6 w-full max-w-md">
        {result && (
          <div className="bg-white p-4 rounded-lg shadow-sm">
            <h2 className="text-lg font-bold text-gray-700">Generated Text:</h2>
            <p className="mt-2">{result}</p>
          </div>
        )}
      </div>
      <div className="mt-6 w-full max-w-md">
  {result && (
    <div className="bg-white p-4 rounded-lg shadow-sm">
      <h2 className="text-lg font-bold text-gray-700">Generated Color Palette:</h2>
      <div className="mt-2 flex gap-2">
        {Array.isArray(result) &&
          result.map((color, index) => (
            <div
              key={index}
              className="w-12 h-12 rounded-md"
              style={{ backgroundColor: color }}
              title={color}
            ></div>
          ))}
      </div>
    </div>
  )}
</div>
    </div>
  );
}

export default App;
